#Translating RNA into Protein
from ast import literal_eval
def translateRNA(string):
	f = open("/Users/Azreal/Documents/jcrosalind/Codontable.txt", "r")
	condontable = f.read()
	RNA_dict = literal_eval(condontable) #this will take the text file and make it into a dictionary
	modified = ' '.join([string[i:i+3] for i in range(0, len(string), 3)])
	for key in RNA_dict.keys():
		half_way = modified.replace(key, RNA_dict[key])
	final_string = half_way.replace(" ", "")	
	print (final_string)

translateRNA("")
